package com.nacai.settlement.api.entity.response;/** * Created by shan on 2018/5/29. */public enum ResponseStatusEnum implements ResponseInfoInterface{    INVALID_BIZ_PARAMS("FAIL", "CG320002", ""),    INVALID_PARAMS("FAIL", "CG320002", "参数有误!"),    SYS_ERROR("FAIL", "99999999", "系统异常，请稍后重试！");    ResponseStatusEnum(String status, String code, String msg) {        this.status = status;        this.code = code;        this.msg = msg;    }    /**     * 状态     */    private String status;    /**     * 状态码     */    private String code;    /**     * 描述信息     */    private String msg;    public String getStatus() {        return status;    }    public void setStatus(String status) {        this.status = status;    }    public String getCode() {        return code;    }    public void setCode(String code) {        this.code = code;    }    public String getMsg() {        return msg;    }    public void setMsg(String msg) {        this.msg = msg;    }}