package com.nacai.settlement.server.impl;import com.alibaba.dubbo.config.annotation.Service;import com.nacai.settlement.api.domain.City;import com.nacai.settlement.api.service.ICityService;import com.nacai.settlement.server.dao.CityMapper;import org.springframework.beans.factory.annotation.Autowired;/** * Created by shan on 2018/5/29. */// 注册为 Dubbo 服务@Service(version = "1.0.0")public class CityServiceImpl implements ICityService {    @Autowired    CityMapper cityMapper;    @Override    public City selectByPrimaryKey(Integer id) {        return cityMapper.selectByPrimaryKey(id);    }}