package com.nacai.settlement.server.utils;import java.text.SimpleDateFormat;import java.util.Calendar;import java.util.Date;/** * Created by shan on 2018/5/29. */public class DateUtils {    /**     * 获取当前时间的日期     *bank-depository-agent     * @return     */    public static String getCurrentDateStr() {        SimpleDateFormat format = new SimpleDateFormat("yyyyMMdd");        Calendar calendar = Calendar.getInstance();        Date today = calendar.getTime();        return format.format(today);    }    /**     * 获取当前的时间     *     * @return     */    public static String getCurrentTimeStr() {        SimpleDateFormat format = new SimpleDateFormat("HHmmss");        Calendar calendar = Calendar.getInstance();        Date today = calendar.getTime();        return format.format(today);    }}