package com.nacai.settlement.server.utils;import java.math.BigDecimal;/** * Created by shan on 2018/5/29. */public class MathUtils {    /**     * BigDecimal 转 String 2位小数     *     * @param source     * @return     */    public static String convertDecimal2String(BigDecimal source) {        if (source == null) {            return "0.00";        }        source = source.setScale(2, BigDecimal.ROUND_HALF_UP);        return source.toString();    }}