package com.nacai.settlement.web.service;import com.alibaba.dubbo.config.annotation.Reference;import com.nacai.settlement.api.domain.City;import com.nacai.settlement.api.entity.request.TempletParams;import com.nacai.settlement.api.entity.response.ResponseEntity;import com.nacai.settlement.api.service.ICityService;import com.nacai.settlement.api.service.ITempletService;import org.springframework.stereotype.Component;/** * Created by shan on 2018/5/29. */@Componentpublic class TempletConsumerService {    @Reference(version = "1.0.0", timeout = 30000)    ITempletService iTempletService;    @Reference(version = "1.0.0", timeout = 30000)    ICityService iCityService;    public ResponseEntity queryTemplet(TempletParams templetParams) {        return iTempletService.queryTemplet(templetParams);    }    public ResponseEntity queryCityById(Integer id) {        ResponseEntity responseEntity = new ResponseEntity();        City city = iCityService.selectByPrimaryKey(id);        responseEntity.setData(city);        return responseEntity;    }}